package test.ipo.task1.service;import org.testng.Assert;import org.testng.annotations.*;import by.ipo.task1.service.DecimalNumTurner;public class DecimalNumTurnerTest {	private DecimalNumTurner dmt = DecimalNumTurner.getInstance();		@DataProvider(name = "numberTurnData")	public Object[][] setData() {		return new Object[][] {				{ 111.222, new double[] {111.222, 222.111} },				{ 333.888, new double[] {333.888, 888.333} },			};	}		@DataProvider(name = "numberTurnWrongData")	public Object[][] setWrongData() {		return new Object[][] {				{ 3353.8883, new NumberFormatException() },				{ -335.883, new NumberFormatException() },				{ 3.14, new NumberFormatException() },			};	}		@Test(description = "Проверка переворота чисел", 		  dataProvider = "numberTurnData")	public void turnTest(double num, double[] expectedAnswer) {		Assert.assertEquals(dmt.turn(num), expectedAnswer);	}		@Test(description = "Проверка переворота чисел", 			  dataProvider = "numberTurnWrongData", 			  expectedExceptions = NumberFormatException.class)	public void turnTestWrong(double num, 							  NumberFormatException expectedAnswer) {		Assert.assertEquals(dmt.turn(num), expectedAnswer);	}}