package test.ipo.task1.service;import java.io.IOException;import org.testng.Assert;import org.testng.annotations.*;import by.ipo.task1.service.ExpressionCalculation;public class ExpressionCalculationTest {	private ExpressionCalculation ec = ExpressionCalculation.getInstance();		@DataProvider(name = "expressionData")	public Object[][] setData(){		return new Object[][] {								{ 2, 2, 2, 2, 0.5, 14.0 },								{ -2, 2, -2, 2, 0.5, 6.0 },								{ -4.5, 2.2, -1.3, 8.8, 0.11363636363636365, 								330.42 } 		};	}		@Test(description = "Проверка вычисления формулы", 		  dataProvider = "expressionData")	public void calculateFirstExpressionTest(double a, double b, double c, 											 double d, double expectedAnswer,											 double expectedAnswer2) 													 throws IOException {		Assert.assertEquals(ec.calculateFirstExpression(a, b, c, d), 							expectedAnswer);	}		@Test(description = "Проверка вычисления квадратичной формулы", 						dataProvider = "expressionData")	public void calculateSquareExpressionAbsTest(double a, double b, double c, 												 double x,			 									 double expectedAnswer,			 									 double expectedAnswer2) {		Assert.assertEquals(ec.calculateSquareExpressionAbs(a, b, c, x), 							expectedAnswer2);	}}