package by.ipo.task1.service;import org.apache.logging.log4j.LogManager;/** * This class provides methods to swap variables. * @author Pavel Isidovich * */public class SwapVariables {		private static SwapVariables instance;	private static org.apache.logging.log4j					.Logger logger = LogManager.getFormatterLogger();		private SwapVariables() {			}		/**	 * This method returns link on existing object of this class. If	 * object doesn't exist, creates new object of this class.	 * @return <strong>instance</strong> of object.	 */	public static SwapVariables getInstance() {		if (instance == null) {			instance = new SwapVariables();		}		return instance;	}		/**	 * This method swaps variables through dump-variable.	 * @param first - first integer variable	 * @param second - second integer variable	 * @return array, where element[0] - first integer variable, 	 * element[1] - second integer variable	 */	public int[] swapFirstWay(int first, int second) {					logger.info("Данные получены");						int temp = first;			first = second;			second = temp;						logger.info("Ответ отправлен");						return new int[] {first, second};	}	/**	 * This method swaps variables through dump-variable.	 * @param first - first integer variable	 * @param second - second integer variable	 * @return array, where element[0] - first integer variable, 	 * element[1] - second integer variable	 */	public int[] swapSecondWay(int first, int second) {		logger.info("Данные получены");		first = first + second;		second = first - second;		first = first - second;					logger.info("Ответ отправлен");					return new int[] {first, second};	}		/**	 * This method swaps variables through XOR operation	 * @param data - variables in string format	 * @return string-answer	 */	public int[] swapThirdWay(int first, int second) {			logger.info("Данные получены");			first = first ^ second;			second = second ^ first;			first = first ^ second;						logger.info("Ответ отправлен");						return new int[] {first, second};	}}