package test.ipo.task1.service;import java.io.IOException;import org.testng.Assert;import org.testng.annotations.*;import by.ipo.task1.service.DateAndMonthReveal;public class DateAndMonthRevealTest {	private DateAndMonthReveal damr = DateAndMonthReveal.getInstance();		@DataProvider(name = "dayOfYearData")	public Object[][] setData() {		return new Object[][] {				{ 1, new int[] {1, 1} },				{ 33, new int[] {2, 2} },				{ 365, new int[] {31, 12} }		};	}		@DataProvider(name = "dayOfYearWrongData")	public Object[][] setWrongData() {		return new Object[][] {				{ 888, new IOException()},				{ 0, new IOException()},				{ -10, new IOException()}		};	}		@Test(description = "Проверка обнаружения числа и месяца", 		  dataProvider = "dayOfYearData")	public void getDateAndMonthTest(int day, int[] expectedAnswer) 			throws IOException {		Assert.assertEquals(damr.getDateAndMonth(day), expectedAnswer);	}		@Test(description = "Проверка обнаружения числа и месяца", 			  dataProvider = "dayOfYearWrongData", 			  expectedExceptions = IOException.class)	public void getDateAndMonthTestWrong(int day, IOException expectedAnswer) 			throws IOException {		Assert.assertEquals(damr.getDateAndMonth(day), expectedAnswer);	}}