package test.ipo.task1.service;import org.testng.Assert;import org.testng.annotations.DataProvider;import org.testng.annotations.Test;import by.ipo.task1.service.ModuleAccessCheck;public class ModuleAccessCheckTest {	private ModuleAccessCheck mac = ModuleAccessCheck.getInstance();		@DataProvider(name = "baseAccessData")	public Object[][] setData() {		return new Object[][] {								{"9583", 3},								{"3331", 2},								{"8997", 1},								{"354f", 0},							  };	}		@Test(description = "Проверка выдачи доступа к модулям базы данных",		  dataProvider = "baseAccessData")	public void checkAccessTest(String password, int expectedAnswer) {		Assert.assertEquals(mac.checkAccess(password), expectedAnswer);	}}